import * as React from 'react';
import TestRenderer from 'react-test-renderer';
import { createHostComponent } from '../createHostComponent';
describe('createHostComponent', function () {
    it('create correctly', function () {
        var RemaxCustomComponent = createHostComponent('custom-component');
        var testRenderer = TestRenderer.create(React.createElement(RemaxCustomComponent, { foo: "bar" }));
        expect(testRenderer.toJSON()).toMatchSnapshot();
    });
    it('create with custom render', function () {
        var RemaxCustomComponent = createHostComponent('custom-component', function (props) {
            return React.createElement('input', props);
        });
        var testRenderer = TestRenderer.create(React.createElement(RemaxCustomComponent, { foo: "bar" }));
        expect(testRenderer.toJSON()).toMatchSnapshot();
    });
});
